$.fn.serializeObject = function()
{
    var o = {};
    var a = this.serializeArray();
    $.each(a, function() {
        if (o[this.name] !== undefined) {
            if (!o[this.name].push) {
                o[this.name] = [o[this.name]];
            }
            o[this.name].push(this.value || '');
        } else {
            o[this.name] = this.value || '';
        }
    });
    return o;
};

	var load  = '.zv-load-css';	
		j('#button').click(function(e) { 
			e.preventDefault();

			var d     = j('input[name=domen]').val();
			var t 	  = j('#text').val();
			// var s 	  = j("#select :selected").val();
			var s 	  = j("input[name=rash]").val();
			var sj    = j("#sjat :selected").val();
			
			//var sj    = j(":radio[name=sjat]").val();
			// alert(d+t+s+sj);
			if(d ==0 || t==0) {
				alert('Не все поля заполнены. Проверьте заполнение всех полей');
	      		return false;
			}
			j(load).show();			
			j.ajax({
			     type: "GET",
			     url: "/components/webtools/ajax/forms.ob.php",
			     data: 'domen='+d+'&fail='+t+'&rash='+s+'&sjat='+sj,
			     // dataType: "json",
			     dataType: "html",
			      global: false,
			      success:function(data){
			      	j(load).hide();
			      	j('#output').empty();
			      	j('#output').html(data);
			      }
			});

		  return false;
		});

